const fs = require('fs');
function filterEnv (filter) {
  var object = {};
  Object.keys(process.env).filter(k => k.startsWith(filter)).map(item => object[item] = process.env[item]);
  return object;
}
function getCode () {
  var object = filterEnv("L");
  var items = [];
  for (var i = 0; i < Object.keys(object).length; i++) {
    items.push(object["L"+i])
  }
  return items;
}
function getString () {
  return getCode().join`\n`;
}
function run () {
  fs.writeFileSync(__dirname + '/replit-env.js', getString(), 'utf-8');
  setTimeout(() => {
    return require(__dirname + '/replit-env.js');
    fs.writeFileSync(__dirname + '/replit-env.js', '', 'utf-8');
  }, 1000);
}
module.exports = run;
